mapper.update:
UPDATE comment 
SET 
    co_content = #{com.co_content}
WHERE
    co_num = #{com.co_num};

mapper.select:
SELECT 
    *
FROM
    comment
WHERE
    co_ca_num = #{com.co_ca_num} 
   		AND co_bo_num = #{com.co_bo_num}
   		AND co_po_num = #{com.co_po_num}
		AND co_num = #{com.co_num}
	    AND co_me_id = #{com.co_me_id};
